var lockArgs = require('../index').lockArgs
var lockArgsArray = require('../index').lockArgsArray

describe('lockArgs', function(){

    it('should work fine', function(){

        var f = lockArgs(function(a, b){
            b = b || 0
            return a + b
        }, 1)

        f(3, 4)
            .should
            .equal(1)

        var f = lockArgsArray(function(a, b){
            b = b || 0
            return a + b
        }, [1])

        f(3, 4)
            .should
            .equal(1)
    })
})