describe('Max args', function(){

    var maxArgs = require('../index').maxArgs

    it('should find max args 1', function(){
        var first, second, third

        function log(a,b,c){
            first  = a
            second = b
            third  = c || 'ZZZ'
        }

        log = maxArgs(log, 2)

        log(1,2,3)

        first.should.equal(1)
        second.should.equal(2)
        third.should.equal('ZZZ')

    })

    it('should work well with parseInt', function(){
        ['1','2','3'].map(
            maxArgs(parseInt,1)
        )
        .should
        .eql([1, 2, 3])
    })

})