import React, {PropTypes} from 'react';
export default class svg extends React.Component {
  componentWillMount() {
    this.ajaxLoad();
  }
  ajaxLoad() {
    const ajax = new XMLHttpRequest();
      ajax.open("GET", this.props.src, true);
      ajax.send();
      ajax.onloadend = function(e) {
        for (let value of e.target.responseXML.firstChild.childNodes) {
            if(value.nodeType === 1)
              this.refs.svg.appendChild(value)
          }
    }.bind(this)
  }
  render() {
    let { onClick, className, width, height, id, viewBox, preserveAspectRatio } = this.props
    return (
      <svg ref='svg' xmlns="http://www.w3.org/2000/svg" version="1.1" viewBox={viewBox} preserveAspectRatio={preserveAspectRatio || "xMinYMin meet" }  onClick={onClick} id={id} width={width} height={height} className={className}></svg>
    )
  }
}
svg.propTypes = {
  src: React.PropTypes.string.isRequired
};
