describe('toLowerFirst', function(){

    var alphanum = require('../index').is.alphanum

    it('should invalidate other characters', function(){

        alphanum('ABc%')
            .should
            .equal(false)

        alphanum('ABc-d')
            .should
            .equal(false)

        alphanum('ABc%')
            .should
            .equal(false)
    })

    it('should validate alphanum strings', function(){

        alphanum('a0BZ')
            .should
            .equal(true)
    })
})